package com.aayushpuranik.jokesandmemesapp.Repositoryimport com.aayushpuranik.jokesandmemesapp.services.ApiServiceimport com.aayushpuranik.jokesandmemesapp.views.NetworkStateimport javax.inject.Injectclass JokesRepository @Inject constructor(val apiService: ApiService) {    suspend fun getCategories(): NetworkState<List<String>?> {        val response = apiService.getCategories()        if(response?.isNotEmpty() == true) {            return NetworkState.Success(response)        } else {            return NetworkState.Error("Something went wrong. Please try again later")        }    }}