package com.aayushpuranik.jokesandmemesapp.diimport com.aayushpuranik.jokesandmemesapp.services.ApiServiceimport com.jakewharton.retrofit2.converter.kotlinx.serialization.asConverterFactoryimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport kotlinx.serialization.json.Jsonimport okhttp3.MediaType.Companion.toMediaTypeimport retrofit2.Retrofitimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object NetworkModule {    @Provides    fun providesBaseUrl(): String = "https://api.chucknorris.io/jokes/"    @Provides    @Singleton    fun provideRetrofit(baseUrl: String): Retrofit{        val json = Json {            ignoreUnknownKeys = true            isLenient = true            encodeDefaults = true        }     return Retrofit.Builder()            .baseUrl(baseUrl)            .addConverterFactory(json.asConverterFactory("application/json".toMediaType()))            .build()    }    @Provides    @Singleton    fun provideApiService(retrofit: Retrofit): ApiService {        return retrofit.create(ApiService::class.java)    }}